#### 미정의 문자 변환표
indeterminate_code = {\
# 'brf': ('brf', 'indeterminate letter')
'_^_^': ('_^_^', '^^'), 'a': ('a', '^1^'), 'b': ('b', '^12^'), 'l': ('l', '^123^'), 'p': ('p', '^1234^'), 'q': ('q', '^12345^'), '=': ('=', '^123456^'), '&': ('&', '^12346^'), 'r': ('r', '^1235^'), '(': ('(', '^12356^'), 'v': ('v', '^1236^'), 'f': ('f', '^124^'), 'g': ('g', '^1245^'), '}': ('}', '^12456^'), '$': ('$', '^1246^'), 'h': ('h', '^125^'), '|': ('|', '^1256^'), '<': ('<', '^126^'), 'k': ('k', '^13^'), 'm': ('m', '^134^'), 'n': ('n', '^1345^'), 'y': ('y', '^13456^'), 'x': ('x', '^1346^'), 'o': ('o', '^135^'), 'z': ('z', '^1356^'), 'u': ('u', '^136^'), 'c': ('c', '^14^'), 'd': ('d', '^145^'), '?': ('?', '^1456^'), '%': ('%', '^146^'), 'e': ('e', '^15^'), ':': (':', '^156^'), '*': ('*', '^16^'), '1': ('1', '^2^'), '2': ('2', '^23^'), 's': ('s', '^234^'), 't': ('t', '^2345^'), ')': (')', '^23456^'), '!': ('!', '^2346^'), '6': ('6', '^235^'), '7': ('7', '^2356^'), '8': ('8', '^236^'), 'i': ('i', '^24^'), 'j': ('j', '^245^'), 'w': ('w', '^2456^'), '{': ('{', '^246^'), '3': ('3', '^25^'), '4': ('4', '^256^'), '5': ('5', '^26^'), '\'': ('\'', '^3^'), '/': ('/', '^34^'), '>': ('>', '^345^'), '#': ('#', '^3456^'), '+': ('+', '^346^'), '9': ('9', '^35^'), '0': ('0', '^356^'), '-': ('-', '^36^'), '`': ('`', '^4^'), '^': ('~', '^45^'), '_': ('_', '^456^'), '.': ('.', '^46^'), '"': ('"', '^5^'), ';': (';', '^56^'), ',': (',', '^6^')
}

# 
def translate_brf_to_il(brf):
    empty_str = ""
    for i in brf:
        if i in indeterminate_code: empty_str += indeterminate_code[i][1]
        else: empty_str += i
    result = empty_str.replace("^^", ",")
    return result